const { BridgeSDK, TOKEN, EXCHANGE_MODE, STATUS } = require('..');
const configs = require('../lib/configs');

const operationCall = async () => {
  const bridgeSDK = new BridgeSDK({ logLevel: 2 }); // 2 - full logs, 1 - only success & errors, 0 - logs off

  await bridgeSDK.init(configs.testnet);

  await bridgeSDK.addOneWallet(
    '0x991ab43646aa8ac69a801c556aefe50baeca9b47f67c3579d2e6f54a951a84c6'
  );

  let operationId;

  // display operation status
  let intervalId = setInterval(async () => {
    if (operationId) {
      const operation = await bridgeSDK.api.getOperation(operationId);

      /*
      console.log(operation.status);
      console.log(
        'Action: ',
        operation.actions.filter(a => a.status === STATUS.IN_PROGRESS)
      );
      */

      if (operation.status !== STATUS.IN_PROGRESS) {
        clearInterval(intervalId);
        process.exit();
      }
    }
  }, 4000);

  try {
    await bridgeSDK.sendToken(
      {
        type: EXCHANGE_MODE.ONE_TO_ETH,
        token: TOKEN.ERC721,
        amount: [502],
        erc20Address: '0x16d13B00bF2F6588D940EEAd5C1189cD1D614874',
        oneAddress: 'one1437j7lqq54v9ng7qqs0x7cf42tqkcz4thckwua',
        ethAddress: '0x430506383F1Ac31F5FdF5b49ADb77faC604657B2',
      },
      id => (operationId = id)
    );
  } catch (e) {
    console.log('Error: ', e.message);
  }

  process.exit();
};

operationCall();
